import React, { useState, useEffect } from 'react'
import './App.css'
import Spotify from 'spotify-web-api-js';

const spotifyWebApi = new Spotify(); 

function App () {
  const [auth, checkAuth] = useState(false)
  const [nowPlaying, setNowPlaying] = useState({name: 'Not Checked', img: ''})
  useEffect(() => {
    console.log(params)
    console.log('authenticated: ', auth)
    console.log(nowPlaying)

    if (params.access_token) {
      spotifyWebApi.setAccessToken(params.access_token)
    }
  }, [nowPlaying])
  const getHashParams = () => {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }
  const getNowPlaying = () => {
    spotifyWebApi.getMyCurrentPlaybackState()
    .then((res) => {
      setNowPlaying({name: res.item.name, img: res.item.album.images[0].url})
    })
  }
  const params = getHashParams()
  return (
    <div className="App">
      <div className="Intro-Text">
        Welcome to Visualize-Spotify!
        <div style={{ fontSize: "20px", fontWeight: "300" }}>Login to start</div>
      </div>
      <div className="Login-Button">
        <a id="login" href="http://localhost:8888/login">
          <button id="auth-button">LOGIN WITH SPOTIFY</button>
        </a>
      </div>
      <div style={{textAlign: 'center', fontFamily: 'Avenir'}}>
        <div style={{color: 'white'}}>Now Playing: {nowPlaying.name}</div>
        <img style={{padding: '20px'}} src={nowPlaying.img} style={{width: '100px'}}/>
        <div>
          <button onClick={getNowPlaying}>
            Get Current Playing
          </button>
        </div>
      </div>
    </div>
  )
}

export default App
